const numbers = [1, 2, 3, 4, 5];
const doubledEvens = [];
for (let i = 0; i < numbers.length; i++) {
  if (numbers[i] % 2 === 0) {
    doubledEvens.push(numbers[i] * 2);
  }
}
console.log(doubledEvens); // [4, 8]

const numbers = [1, 2, 3, 4, 5];
const doubledEvens = numbers.filter(n => n % 2 === 0).map(n => n * 2);
console.log(doubledEvens); // [4, 8]
